import logo from './logo.svg';
import './App.css';
import { useForm, Controller } from "react-hook-form";

function App() {

  const { register, handleSubmit, formState: { errors } } = useForm();
  const onSubmit = (data, e) => console.log(data, e);
  const onError = (errors, e) => console.log(errors, e);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <form onSubmit={handleSubmit((data) => console.log(data))} className="App-form">
          <label>Text</label>
          <textarea {...register('text', {required:true})} ></textarea>
          {errors.text && <span className="error">Text is required</span>}
          <label>Word</label>
          <input {...register('word', { required: true })} />
          {errors.word && <p>A word is required.</p>}
          <input type="submit" />
        </form>
      </header>
    </div>
  );
}

export default App;
