import logo from './logo.svg';
import './App.css';
import { useForm } from "react-hook-form";

function App() {
  let btnSubmit = {
    text:"Send",
    colorClass:"gold"
  };
  let btnClear = {
    text:"Clear",
    colorClass:"gold"
  };
  const { register, handleSubmit,setValue, formState: { errors } } = useForm();
  const onSubmit = (data, e)=>{
    sendText = "Sent";
    console.log(data);
  }
  const onClear=()=>{
    setValue('text','');
    setValue('word','');

  }
  // const onError = (errors, e) => console.log(errors, e);

  return (
    <div className="App">
       <img src={logo} className="App-logo" alt="logo" />
      <header className="App-header">
        <div className='App-form-container'>
          <form onSubmit={handleSubmit((data,e) => onSubmit(data,e))} className="App-form">
            <span className="form-item">
              <label>Text</label>
              <textarea rows={10} {...register('text', { required: true })} ></textarea>

            </span>
            <span className="form-item">
              <label>Word</label>
              <input {...register('word', { required: true })} />

            </span>
            <span className="form-item">
              <input type="submit" value={btnSubmit.text} />
            </span>
            <span className="form-item">
              <input type="button" className={btnClear.colorClass} value={btnClear.text} onClick={onClear} />
            </span>
          </form>
        </div>
        <div className='App-result'>
        <div className='App-errors'>
          {errors.text && <span className="errorText">Text is required</span>}
          {errors.word && <span className='errorText'>A word is required.</span>}
        </div>
        </div>
      </header>
    </div>
  );
}

export default App;
